mapper.select:
SELECT DISTINCT i.gathering_id
FROM interest i

    WHERE i.interest_name IN (
    SELECT interest_name
    FROM interest
    WHERE member_id = #{memberId}
    )
    AND i.gathering_id IS NOT NULL
    AND i.gathering_id NOT IN (
    SELECT gathering_id
    FROM gathering_member
    WHERE member_id = #{memberId}
    )
